$(function(){$(".login-btn").on("tap",function(){var c=$("#username").val();var a=$("#password").val();var b={username:c,password:a};if(c.length==0){mui.toast("用户名不能为空");return}if(a.length==0){mui.toast("密码不能为空");return}$.ajax({type:"post",url:"/api/user/login",datType:"json",data:b,success:function(d){if(d.success){if(location.search&&location.search.indexOf("?returnUrl=")>-1){var e=location.search.replace("?returnUrl=","");location.href=e}else{location.href="index.html"}window.sessionStorage.setItem("user","true")}else{mui.toast("账号密码错误")}}})});$("#goReg").on("tap",function(){mui.openWindow({url:"reg.html",id:"reg"})})});