$(function(){search();var a=sn.getUrlParams().productId;getProcuct({id:a},function(c){$(".main").text(c.proName);$(".myMain").html(template("bannerTemplate",c));$("img").lazyload({placeholder:"images/loading.gif",effect:"fadeIn",threshold:180});var b=mui(".mui-slider");b.slider({interval:0});var c={productId:a,size:1,num:1};if(!c.productId){mui.toast("商品异常");return false}$(".cart").on("tap",function(){sn.loginAjax({type:"post",url:"/api/cart/addCart",data:c,success:function(d){if(d.success){mui.toast("加入购物车成功")}else{mui.toast("添加失败，请重试！")}},error:function(){mui.toast("网络繁忙")}})})})});var search=function(){window.onscroll=function(){var a=$(window).scrollTop();if(a>10){$(".sn_header").addClass("scroll")}else{$(".sn_header").removeClass("scroll")}}};var getProcuct=function(a,b){$.ajax({type:"get",url:"/api/product/queryProductDetail",data:a,dataType:"json",success:function(c){console.log(c);b&&b(c)}})};